INSERT INTO `posts` (`id`, `descricao`, `titulo`, `arq`, `file_name`, `enabled`) VALUES
	(1, 'Big Data: você provavelmente já ouviu falar nisso, não é mesmo? Mas, você sabe o que significa? De onde surgiu? Por que se fala tanto sobre nos últimos anos e porque as empresas do mundo todo tem os olhos voltados para ela?\n\nBem, o termo está diretamente relacionado aos dados gerados por pessoas e também empresas diariamente. Para você ter ideia, estima-se que sejam gerados diariamente 2,5 quintilhões de bytes de dados, segundo a IDC. Essas informações não são apenas organizacionais, mas também refletem o comportamento do seu potencial cliente.\n\nNão é à toa que os dados estão sendo chamados de “o novo petróleo”. Afinal, na atualidade, quem tem informação tem também mais opções de estratégias. Dessa forma, com a evolução da tecnologia e a transformação digital, vão surgindo novas formas de analisar esses dados e transformá-los em serviços diferenciados.\n\nMas a verdade é que o Big Data já existe até mesmo antes da popularização dos meios digitais. Por isso, veja neste artigo completo o que é Big Data, como ele surgiu e como funciona!', 'Big Data', NULL, NULL, 0),
	(2, 'Big Data: você provavelmente já ouviu falar nisso, não é mesmo? Mas, você sabe o que significa? De onde surgiu? Por que se fala tanto sobre nos últimos anos e porque as empresas do mundo todo tem os olhos voltados para ela?\n\nBem, o termo está diretamente relacionado aos dados gerados por pessoas e também empresas diariamente. Para você ter ideia, estima-se que sejam gerados diariamente 2,5 quintilhões de bytes de dados, segundo a IDC. Essas informações não são apenas organizacionais, mas também refletem o comportamento do seu potencial cliente.\n\nNão é à toa que os dados estão sendo chamados de “o novo petróleo”. Afinal, na atualidade, quem tem informação tem também mais opções de estratégias. Dessa forma, com a evolução da tecnologia e a transformação digital, vão surgindo novas formas de analisar esses dados e transformá-los em serviços diferenciados.\n\nMas a verdade é que o Big Data já existe até mesmo antes da popularização dos meios digitais. Por isso, veja neste artigo completo o que é Big Data, como ele surgiu e como funciona!', 'Big data', NULL, NULL, 0),
	(3, 'Aurora Aksnes (Stavanger, 15 de junho de 1996), conhecida profissionalmente apenas como Aurora e às vezes estilizado em caixa alta como AURORA, é uma cantora e compositora norueguesa.[2] Seu EP de estreia "Running with the Wolves" foi lançado pela gravadora Decca Records em maio de 2015,[3] recebendo grande aprovação de blogueiros de música e da imprensa. A artista já gravou dois covers de maior visibilidade, o primeiro de "Half the World Away", da banda Oasis, para uma propaganda Natalina de John Lewis e o segundo cover da música "Nature Boy" de Nat King Cole.[4] Ganhou algum maior reconhecimento a partir de uma publicação de Katy Perry ao dizer que havia gostado da música "Runaway".[5]', 'Aurora', NULL, NULL, 0),
